import React from 'react';
import '../App.css';

const coursesList = [
	{
		title: 'Docker Mastery: with Kubernetes + Swarm',
		image: 'docker_mastery',
		link: '',
	},
	{
		title: 'Docker Mastery: with Kubernetes + Swarm',
		image: 'docker_mastery',
		link: '',
	},
	{
		title: 'Docker Mastery: with Kubernetes + Swarm',
		image: 'docker_mastery',
		link: '',
	},
	{
		title: 'Docker Mastery: with Kubernetes + Swarm',
		image: 'docker_mastery',
		link: '',
	},
];

class Courses extends React.Component {
	CoursesList = () => {
		return (
			<div className='courses-container'>
				{coursesList.map(item => {
					return (
						<a href={item.link} className='course-item-container'>
							<img
								src={require(`../assets/${item.image}.png`)}
								className='course-image'
							/>
							<div className='course-title'>
								<h3>{item.title}</h3>
							</div>
						</a>
					);
				})}
			</div>
		);
	};
	render() {
		return (
			<div className='courses'>
				<h2>My Courses</h2>
				<p>
					I focus exclusively on Container DevOps practices and tools.
					This means Docker, Kubernetes, Swarm, Compose, Registry, and
					more! Get all my courses for the best current price each
					with coupon links below.
				</p>
				<this.CoursesList />
			</div>
		);
	}
}

export default Courses;
